const pdftk = require('./pdftk');
const fs = require('fs');

pdftk
    .input(fs.readFileSync('../test.pdf'))
    .fillForm({
        distnameHeading: 'hello',
    })
    .output('./done.pdf')
    .then(file => {
        console.log('done');
        process.exit(0);
    })
    .catch(err => {
        console.error(err);
        process.exit(1);
    });
